const inputData = items[0].json;

// Инициализация переменных
const { count_id, name, count, weigth } = inputData;
const result = [];

// Функция для определения правильного склонения слова "перевозка"
function getDeclension(num) {
    if (num === 1) return 'перевозка';
    if (num > 1 && num < 5) return 'перевозки';
    return 'перевозок';
}

// Определение глагола в зависимости от числа перевозок
const verb = count_id === 1 ? 'была' : 'было';


result.push(`<b>В работе ${verb} ${count_id} ${getDeclension(count_id)}:</b>\n`);
for (let i = 0; i < count_id; i++) {
    result.push(`<b>${i + 1}) ${name[i]}:</b>\nКоличество рейсов: ${count[i]}\nСумма нетто: ${weigth[i]}\n`);
    }


// Объединяем строки в одну
const output = result.join('\n');

// Возвращаем результат
return [{ json: { output } }];
