let combinedMessage = "";
let c = 1;

for (const item of $input.all()) {
  const transportation = item.json;
  const sender = transportation.sender[1];
  const partner = transportation.partner[1];
  const route = transportation.display_name.substring(11);
  const material = transportation.material[1];
  const comment = transportation.comment;
  const endCondition = transportation.end_condition;
  const requiredCubicMetre = transportation.required_cubic_metre;
  const round_quantity_for_car= transportation.round_quantity_for_car;
  const required_ton= transportation.required_ton;

  let message = c + `. Отправитель - <b>${sender}</b>\nПолучатель - <b>${partner}</b>\nМаршрут - <b>${route}</b>\nМатериал - <b>${material}</b>`;

  if (comment !== false) {
    message += `\nКомментарий - <b>${comment}</b>`;
  }

  if (endCondition === "По перевезенным кубометрам") {
    message += `\nОстаток кубометров по перевозке - <b>${requiredCubicMetre}</b>`;
  }

  if (endCondition === "Количество рейсов для каждого автомобиля") {
    message += `\nТребуемое количество рейсов по перевозке - <b>${round_quantity_for_car}</b>`;
  }

  if (endCondition === "По перевезенным тоннам") {
    message += `\nТребуемое количество тонн по перевозке - <b>${required_ton}</b>`;
  }

  combinedMessage += message.trim();
  combinedMessage += "\n\n"
  c += 1;
}

return [{ json: { combinedMessage: combinedMessage } }];

const items = $input.all();

let allItems = [];

for (let i = 0; i < items.length; i++) {
    const { transportations, full_quarry_work } = items[i].json;

    if (transportations && Array.isArray(transportations)) {
        allItems = allItems.concat(transportations);
    }
    if (full_quarry_work && Array.isArray(full_quarry_work)) {
        allItems = allItems.concat(full_quarry_work);
    }
}

allItems = allItems.filter(item => item.hasOwnProperty('sequence'));
allItems.sort((a, b) => a.sequence - b.sequence);

return allItems.map(item => ({ json: item }));

let combinedMessages = "";

let index = 1;
for (const item of $input.all()) {
  const data = item.json;

  const isQuarryWork = data.quarry && data.quarry.length > 1;

  let message = "";

  if (isQuarryWork) {
    const quarry = data.quarry[1];
    const requiredHours = data.required_hours || 0;
    const comment = data.comment;

    message = `<b>${index}.</b> Работа в карьере - <b>${quarry}</b>`;
    if (data.end_condition === "По часам") {
      message += `\nНеобходимое количество часов - <b>${requiredHours}</b>`;
    }
    if (comment !== false) {
      message += `\nКомментарий - <i>${comment}</i>`;
    }
    message += `\n\n`;
  } else {
    const sender = data.sender && data.sender.length > 1 ? data.sender[1] : "";
    const partner = data.partner && data.partner.length > 1 ? data.partner[1] : "";
    const placeFrom = data.place_from && data.place_from.length > 1 ? data.place_from[1] : "";
    const placeTo = data.place_to && data.place_to.length > 1 ? data.place_to[1] : "";
    const material = data.material && data.material.length > 1 ? data.material[1] : "";
    const comment = data.comment;
    const endCondition = data.end_condition;
    const requiredCubicMetre = data.required_cubic_metre || 0;
    const requiredTon = data.required_ton || 0;

    let route = placeFrom && placeTo ? `${placeFrom}-${placeTo}` : "";

    message = `<b>${index}.</b>`;

    if (sender) message += ` Отправитель - <b>${sender}</b>`;
    if (partner) message += `\nПолучатель - <b>${partner}</b>`;
    if (route) message += `\nМаршрут - <b>${route}</b>`;
    if (material) message += `\nМатериал - <b>${material}</b>`;

    if (comment !== false) {
      message += `\nКомментарий - <i>${comment}</i>`;
    }

    if (endCondition === "По перевезенным кубометрам" && requiredCubicMetre > 0) {
      message += `\nТребуемое количество кубометров по перевозке - <b>${requiredCubicMetre}</b>`;
    }

    if (endCondition === "По перевезенным тоннам" && requiredTon > 0) {
      message += `\nТребуемое количество тонн по перевозке - <b>${requiredTon}</b>`;
    }

    if (endCondition === "Количество рейсов для каждого автомобиля" && data.round_quantity_for_car > 0) {
      message += `\nКоличество рейсов для каждого автомобиля - <b>${data.round_quantity_for_car}</b>`;
    }

    message += (item.json.concatenated_driver1 ? "\n\nВодители, выполняющие перевозку с вами:\n" + item.json.concatenated_driver1 : "");

    message += `\n\n`;
  }


  combinedMessages += message;
  index++;
}

return [{ json: { combinedMessages: combinedMessages.trim() } }];
