for item in _input.all():
    days = abs(item.json.timeDifference)
    if days % 100 // 10 == 1:
        item.json.sklonenie = "дней"
    elif days % 10 == 1:
        item.json.sklonenie = "день"
    elif days % 10 in [2, 3, 4]:
        item.json.sklonenie = "дня"
    else:
        item.json.sklonenie = "дней"
