// Функция для получения номера недели
function getWeek(date) {
    const current = new Date(date);
    const start = new Date(current.getFullYear(), 0, 1);
    const diff = (current - start + (start.getTimezoneOffset() - current.getTimezoneOffset()) * 60000) / 86400000;
    return Math.floor((diff + start.getDay() + 1) / 7);
}

// Функция для получения начала недели (понедельник)
function getStartOfWeek(date) {
    const current = new Date(date);
    const first = current.getDate() - current.getDay() + 1; // Понедельник
    return new Date(current.setDate(first));
}

// Функция для получения конца недели (воскресенье)
function getEndOfWeek(date) {
    const current = new Date(date);
    const last = current.getDate() - current.getDay() + 7; // Воскресенье
    return new Date(current.setDate(last));
}

// Группируем данные по неделям
function groupByWeek(data) {
    const weeks = {};
    data.forEach(entry => {
        const weekNumber = getWeek(entry.date);
        if (!weeks[weekNumber]) {
            weeks[weekNumber] = [];
        }
        weeks[weekNumber].push(entry);
    });
    return weeks;
}

// Рассчитываем дневную статистику для текущей недели до сегодняшнего дня
function getDailyStats(currentWeekData) {
    const dailyStats = {};
    const startDate = getStartOfWeek(new Date());
    const today = new Date();

    // Итерируемся по каждому дню с начала недели до сегодняшнего дня
    for (let date = new Date(startDate); date <= today; date.setDate(date.getDate() + 1)) {
        const formattedDate = date.toISOString().split('T')[0];
        dailyStats[formattedDate] = { totalTrips: 0, totalEarnings: 0, noTrips: true };
    }

    // Обновляем данные на основе реальных данных
    currentWeekData.forEach(entry => {
        const date = entry.date;
        if (dailyStats[date]) {
            dailyStats[date].totalTrips += entry.count;
            dailyStats[date].totalEarnings += entry.total;
            dailyStats[date].noTrips = false;
        }
    });

    return dailyStats;
}

// Рассчитываем недельную статистику для последних трех недель
function getWeeklyStats() {
    const weeklyStats = [];
    const today = new Date();

    for (let i = 1; i <= 3; i++) {
        const startOfWeek = getStartOfWeek(new Date(today.getFullYear(), today.getMonth(), today.getDate() - i * 7));
        const endOfWeek = getEndOfWeek(new Date(today.getFullYear(), today.getMonth(), today.getDate() - i * 7));
        const weekNumber = getWeek(startOfWeek);
        const weekData = groupedData[weekNumber] || [];

        const totalEarnings = weekData.reduce((acc, entry) => acc + entry.total, 0);
        weeklyStats.push({
            startDate: startOfWeek.toISOString().split('T')[0],
            endDate: endOfWeek.toISOString().split('T')[0],
            totalEarnings: isNaN(totalEarnings) || totalEarnings === 0 ? 0 : totalEarnings
        });
    }

    return weeklyStats;
}

// Преобразуем данные в формат HTML для Telegram
function convertToHTML(currentWeekDailyStats, pastThreeWeeksStats) {
    let result = '';

    result += `<b>Текущая неделя</b>\n`;
    const dates = Object.keys(currentWeekDailyStats).sort((a, b) => new Date(b) - new Date(a));
    dates.forEach(date => {
        const stats = currentWeekDailyStats[date];
        result += `Дата: ${date}\n`;
        if (stats.noTrips) {
            result += `Не было рейсов\n\n`;
        } else {
            result += `Количество рейсов: ${stats.totalTrips}\n`;
            result += `Сумма заработка: ${stats.totalEarnings}\n\n`;
        }
    });

    result += `<b>Отчет за последние 3 недели</b>\n`;
    pastThreeWeeksStats.forEach(stats => {
        result += `Неделя: ${stats.startDate} - ${stats.endDate}\n`;
        result += `Сумма заработка: ${stats.totalEarnings}\n\n`;
    });

    return result;
}

