        // Количество рейсов без прицепа
        const without_trailer_count = total_count - with_trailer_count;

        // Общая сумма за рейсы без прицепа
        const total_without_trailer = without_trailer_count * driver_rate;

        // Общая сумма за рейсы с прицепом
        const total_with_trailer = with_trailer_count * trailer_driver_rate;

        // Общая сумма всех рейсов
        const total = total_without_trailer + total_with_trailer;

        return {
            id: id,
            count: total_count,
            total: total,
            date: date
        };
    });
}

