// Определяем текущую дату
const today = new Date();

// Определяем начало текущей недели (понедельник текущей недели)
const startOfWeek = new Date(today);
startOfWeek.setDate(today.getDate() - today.getDay() + 1); // Понедельник текущей недели

// Определяем начало и конец текущего месяца
const startOfMonth = new Date(today.getFullYear(), today.getMonth(), 1);
const endOfMonth = new Date(today.getFullYear(), today.getMonth() + 1, 0);

// Функция для проверки, попадает ли дата до сегодняшнего дня
function isBeforeToday(dateStr) {
    const date = new Date(dateStr);
    return date >= startOfMonth && date <= today;
}

// Инициализируем объект для хранения данных по дням недели
const weeklyReport = {};
const pastWeeksReport = {};

// Заполняем объект weeklyReport и pastWeeksReport
for (let i = 0; i < 7; i++) {
    const date = new Date(startOfWeek);
    date.setDate(startOfWeek.getDate() + i);
    const dateStr = date.toISOString().split('T')[0];
    weeklyReport[dateStr] = { count: 0, quarry_hours: 0, total: 0 };

    // Инициализируем объект для прошлых недель
    if (date >= startOfMonth && date <= today) {
        const weekStart = new Date(startOfWeek);
        weekStart.setDate(startOfWeek.getDate() - 7); // Начало прошлой недели
        const weekEnd = new Date(startOfWeek);
        weekEnd.setDate(startOfWeek.getDate() - 1); // Конец прошлой недели

        // Добавляем в pastWeeksReport
        while (weekStart >= startOfMonth) {
            const weekStartStr = weekStart.toISOString().split('T')[0];
            pastWeeksReport[weekStartStr] = { count: 0, quarry_hours: 0, total: 0 };
            weekStart.setDate(weekStart.getDate() - 7);
        }
    }
}

// Агрегируем данные для текущей недели
inputData.forEach(entry => {
    if (isBeforeToday(entry.date)) {
        const dateStr = entry.date;
        if (weeklyReport[dateStr]) {
            if (entry.count !== undefined) {
                weeklyReport[dateStr].count += entry.count;
            }
            if (entry.quarry_hours !== undefined) {
                weeklyReport[dateStr].quarry_hours += entry.quarry_hours;
            }
            if (entry.total !== undefined) {
                weeklyReport[dateStr].total += entry.total;
            }
        }
    }
});

// Агрегируем данные для прошлых недель
inputData.forEach(entry => {
