        const weekStart = new Date(date);
        weekStart.setDate(date.getDate() - date.getDay() + 1); // Понедельник недели даты
        const weekStartStr = weekStart.toISOString().split('T')[0];
        if (pastWeeksReport[weekStartStr]) {
            if (entry.count !== undefined) {
                pastWeeksReport[weekStartStr].count += entry.count;
            }
            if (entry.quarry_hours !== undefined) {
                pastWeeksReport[weekStartStr].quarry_hours += entry.quarry_hours;
