const chatId = $('Execute Workflow Trigger').all()[0].json.callback_query.from.id;
const token = '6999092112:AAE0sJ-UZeXwAbOEbQfLWJRL_aK5wTG3Mfo';
const url = `https://api.telegram.org/bot${token}/sendMessage`;

const message = {
    chat_id: chatId,
    text: "Проверьте наличие ТТН и Весовых справок в перевозке.\nВыберите необходимую перевозку🛻",
    reply_markup: inlineKeyboard
};

// Возвращаем данные для запроса
return [
    {
        json: {
            url,
            message
        }
    }
];

let combinedMessage = "";

for (const item of $input.all()) {
  const transportation = item.json;
  const sender = transportation.sender[1];
  const partner = transportation.partner[1];
  const route = transportation.route[1];
  const material = transportation.material[1];
  const comment = transportation.comment;
  const endCondition = transportation.end_condition;
  const requiredCubicMetre = transportation.required_cubic_metre;
  const round_quantity_for_car= transportation.round_quantity_for_car;
  const required_ton= transportation.required_ton;

  let message = `Отправитель - <b>${sender}</b>\nПолучатель - <b>${partner}</b>\nМаршрут - <b>${route}</b>\nМатериал - <b>${material}</b>`;

  if (comment !== false) {
    message += `\nКомментарий - <b>${comment}</b>`;
  }

  if (endCondition === "По перевезенным кубометрам") {
    message += `\nОстаток кубометров по перевозке - <b>${requiredCubicMetre}</b>`;
  }

  if (endCondition === "Количество рейсов для каждого автомобиля") {
    message += `\nТребуемое количество рейсов по перевозке - <b>${round_quantity_for_car}</b>`;
  }

  if (endCondition === "По перевезенным тоннам") {
    message += `\nТребуемое количество тонн по перевозке - <b>${required_ton}</b>`;
  }

  message += "\n\n";

  combinedMessage = message.trim();
}

return [{ json: { combinedMessage: combinedMessage } }];

