// Получаем данные из прошлой ноды
const items = $json["data"];

// Функция для форматирования даты в формат "день.месяц.год"
function formatDate(dateStr) {
    const date = new Date(dateStr);
    const day = String(date.getDate()).padStart(2, '0');
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const year = date.getFullYear();
    return day + '.' + month + '.' + year;
}

// Переменная для сохранения всех сообщений
let combinedMessage = '';

// Обрабатываем каждый элемент и добавляем его к общему сообщению
items.forEach(item => {
    const formattedDate = formatDate(item["date"]);
    let message = '';

    if (item["tech_maintenance"]) {
        message = '<b>🛠У вас запланировано ТО</b> на ' + '<b>' + formattedDate + '</b>';
    } else {
        message = '<b>🛠У вас запланирован ремонт</b> на ' + '<b>' + formattedDate + '</b>';
    }

    // Добавляем сообщение к общему тексту, разделяя их новой строкой
    combinedMessage += message + '\n';
});

// Добавляем переносы строк перед и после итогового сообщения
const finalMessage = '\n' + combinedMessage.trim() + '\n';

// Возвращаем результат в n8n как один элемент с одним сообщением
return [{
    json: {
        message: finalMessage
    }
}];

