function addHoursToTime(timeStr, hours) {
    const dateObj = new Date(timeStr.replace(" ", "T")); // Преобразуем строку времени в объект Date
    dateObj.setHours(dateObj.getHours() + hours); // Добавляем 10 часов
    return dateObj.toISOString().replace("T", " ").split(".")[0]; // Преобразуем обратно в строку и убираем миллисекунды
}

// Функция для подсчета заработка и количества рейсов по датам
function calculateTotalsByDate(data) {
    const totalsByDate = {};

    data.forEach(entry => {
        entry.data.forEach(round => {
            const { driver_rate, driver_with_trailer, time_of_start } = round;

            // Добавляем 10 часов ко времени
            const adjustedTime = addHoursToTime(time_of_start, 10);

            // Извлекаем только дату из скорректированного времени
            const date = adjustedTime.split(" ")[0];

            // Инициализируем данные для каждой новой даты
            if (!totalsByDate[date]) {
                totalsByDate[date] = {
                    total_count: 0,  // Общее количество рейсов
                    total_earnings: 0  // Общий заработок
                };
            }

            // Увеличиваем количество рейсов
            totalsByDate[date].total_count++;

            // Увеличиваем сумму заработка
            totalsByDate[date].total_earnings += driver_rate;
        });
    });

    // Преобразуем объект в массив для удобства обработки
    return Object.keys(totalsByDate).map(date => ({
