// Получаем все элементы из входных данных
const items = $input.all();

// Определяем максимальное количество элементов на одной странице
const maxItemsPerPage = 6;

// Задаем максимальное количество символов для описания заявки
const maxDescriptionLength = 16;

// Получаем номер страницы из callback_data или оставляем 1 по умолчанию
