if (callbackQuery) {
    const callbackData = callbackQuery.data;
    const match = callbackData.match(/page_(\d+)/);
    if (match) {
        page = parseInt(match[1], 10);
    }
    // Получаем message_id из callback_query
    messageId = callbackQuery.message.message_id; // Получаем message_id из объекта callback_query
} else {
    // Если это не callback_query, возвращаемся к стандартному поведению
    messageId = $('Execute Workflow Trigger').all()[0].json.message.message_id; // Получаем message_id из сообщения
}

// Определяем начальный и конечный индексы для отображаемых элементов на странице
const startIndex = (page - 1) * maxItemsPerPage;
const endIndex = startIndex + maxItemsPerPage;

// Определяем, какие элементы будем выводить на текущей странице
const displayItems = items.slice(startIndex, endIndex);

// Функция для обрезки описания с троеточием
const truncateDescription = (description) => {
    if (description.length > maxDescriptionLength) {
        return description.slice(0, maxDescriptionLength) + '…';
    }
    return description;
};

// Формируем массив кнопок с информацией по каждому элементу
const buttons = displayItems.map((item, index) => {
    // Разделяем строку по дефису и оставляем только номер авто (первая часть)
    const carInfo = item.json.car_id[1].split(' - ')[0];
    // Обрезаем описание заявки, если оно слишком длинное
    const truncatedDescription = truncateDescription(item.json.description_of_maintenance);

    return {
        text: `${startIndex + index + 1}. ${carInfo} - ${truncatedDescription}`, // Используем startIndex для правильной нумерации
        callback_data: `item_${item.json.id}_page_${page}` // Добавляем номер страницы
    };
});

// Создаем объект с inline-клавиатурой
const inlineKeyboard = {
    inline_keyboard: [
        ...buttons.map(button => [button]), // Каждая кнопка на своей строке
    ]
};

// Добавляем кнопки "Следующая страница" и "Назад", если нужно
if (items.length > endIndex) {
    inlineKeyboard.inline_keyboard.push([{ text: "Следующая страница", callback_data: `page_${page + 1}` }]);
}
if (page > 1) {
    inlineKeyboard.inline_keyboard.push([{ text: "Предыдущая страница", callback_data: `page_${page - 1}` }]);
}

// Добавляем постоянную кнопку "Назад"
inlineKeyboard.inline_keyboard.push([{ text: "Назад", callback_data: "del_maintenances" }]);

// Логируем для отладки
console.log(JSON.stringify(inlineKeyboard, null, 2));

