// Получаем все элементы из входных данных
const items = $input.all();

// Получаем данные о колбэке из Execute Workflow Trigger
const callbackQuery = $('Execute Workflow Trigger').all()[0].json.callback_query;
const callbackData = callbackQuery ? callbackQuery.data : null;

// Проверяем, был ли получен callbackData
if (!callbackData) {
    throw new Error("Callback data is undefined. Cannot proceed.");
}

// Извлекаем message_id из callback_query
const messageId = callbackQuery.message.message_id;

// Обработка выбора заявки
const selectedMatch = callbackData.match(/item_(\d+)_page_(\d+)/);
if (selectedMatch) {
    const itemId = selectedMatch[1]; // ID выбранной заявки
    const returnPage = parseInt(selectedMatch[2], 10); // Номер страницы для возврата

    // Получаем информацию о выбранной заявке
    const selectedRequest = items.find(item => item.json.id == itemId);
    // Извлекаем необходимые данные из JSON
    const {
        description_of_maintenance,
        car_id,
        created_by,
        id
    } = selectedRequest.json;

    const descriptionText = description_of_maintenance === "Нет описания" 
        ? `${description_of_maintenance} (Смотреть фото в Odoo)` 
        : description_of_maintenance;

    // Формируем текст сообщения
    const carName = car_id[1].split(' - ')[0]; // Получаем только номер авто

    // Ищем данные о водителе
    const driverData = items.find(item => item.json.resource_id && item.json.resource_id[1] === created_by[1]);

    // Если данные о водителе найдены, извлекаем имя и телефон
    const name = driverData ? driverData.json.name : 'Неизвестный водитель';
    const mobile_phone = driverData ? driverData.json.mobile_phone : 'Телефон не указан';

    // Формируем ссылку на заявку в Odoo
    const odooLink = `https://everest.lamart.site/web#id=${itemId}&model=technical.maintenance&view_type=form`;

    // Полное сообщение
    const messageText = `У автомобиля: <b>${carName}</b> проблема:\n\n` +
        `<i>${descriptionText}</i>\n\n`;

    // Получаем chat_id из callback_query
    const chatId = callbackQuery.from.id;

    // Токен бота
    const token = '7062531188:AAFkpyOiRuXLHFQO8mWKHuftPGglQSruqwo';

    // URL для редактирования сообщения через API Telegram
    const url = `https://api.telegram.org/bot${token}/editMessageText`;

    // Создаем объект с inline-клавиатурой в один столбик
    const inlineKeyboard = {
        inline_keyboard: [
            [
                { text: "Взять в работу", callback_data: `TR_${id}` } // Добавляем ID заявки
            ],
            [
                { text: "Назад", callback_data: `page_${returnPage}` } // Устанавливаем номер страницы в колбэк
            ]
        ]
    };

    // Формируем сообщение для редактирования
    const message = {
        chat_id: chatId,
        message_id: messageId,
        text: messageText,
        parse_mode: 'HTML', // Указываем, что текст содержит HTML
        reply_markup: inlineKeyboard // Добавляем inline-клавиатуру
    };

