let combinedMessagesFree = "";
let combinedMessagesNoDriver = "";

for (const item of $input.all()) {
  const data = item.json;
  const driver = data.driver_id ? data.driver_id[1] : null;
  const number_car = data.number_car;
  const status = data.status;

  if (driver && status === "Нет груза") {
    if (!combinedMessagesFree) {
      combinedMessagesFree = 'Сейчас свободны следующие автомобили:\n\n';
    }
    combinedMessagesFree += `<b>${number_car}, ${driver}</b>\n`;
  } else if (!driver && status === "Нет водителя") {
    if (!combinedMessagesNoDriver) {
      combinedMessagesNoDriver = "Текущие автомобили без водителя:\n\n";
    }
    combinedMessagesNoDriver += `<b>${number_car}</b>\n`;
  }
}

let finalMessage = (combinedMessagesFree + "\n" + combinedMessagesNoDriver).trim();

if (!finalMessage) {
  finalMessage = "На данный момент нет свободных автомобилей и автомобилей без водителей";
}

return [{ json: { combinedMessages: finalMessage } }];

