const items = $input.all();

const buttons = items.map((item, index) => ({
    text: `${item.json.partner[1]}`,
    callback_data: `AT_${item.json.partner[1]}` // AT - alert transportation
}));

const inlineKeyboard = {
    inline_keyboard: buttons.map(button => [button])
};

const chatId = $('Execute Workflow Trigger').all()[0].json.inline_chat_id;
const token = '7062531188:AAFkpyOiRuXLHFQO8mWKHuftPGglQSruqwo';
const url = `https://api.telegram.org/bot${token}/sendMessage`;

const message = {
    chat_id: chatId,
    text: "Нажмите на получателя",
    reply_markup: inlineKeyboard
};

// Возвращаем данные для запроса
return [
    {
        json: {
            url,
            message
        }
    }
];

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
var rounds = [];
for (const item of $input.all()) {
  for(const round of item.json.rounds) {
    rounds.push(round);
  }
}

return {"rounds": rounds};
