let combinedMessagesFree = "";
let combinedMessagesNoDriver = "";

const freeCarsByCompany = {};
const noDriverCarsByCompany = {};

for (const item of $input.all()) {
  const data = item.json;
  const driver = data.driver_id ? data.driver_id[1] : null;
  const number_car = data.number_car;
  const status = data.status;
  const company = data.company_owner ? data.company_owner[1] : "Не указана";

  if (driver && status === "Нет груза") {
    if (!freeCarsByCompany[company]) {
      freeCarsByCompany[company] = [];
    }
    freeCarsByCompany[company].push(`<b>${number_car}, ${driver}</b>`);
  } else if (!driver && status === "Нет водителя") {
    if (!noDriverCarsByCompany[company]) {
      noDriverCarsByCompany[company] = [];
    }
    noDriverCarsByCompany[company].push(`<b>${number_car}</b>`);
  }
}

if (Object.keys(freeCarsByCompany).length > 0) {
  combinedMessagesFree = "Сейчас свободны следующие автомобили:\n\n";
  for (const [company, cars] of Object.entries(freeCarsByCompany)) {
    combinedMessagesFree += `<b>${company}</b>: ${cars[0]}\n`;
    if (cars.length > 1) {
      combinedMessagesFree += cars.slice(1).join("\n") + "\n";
    }
    combinedMessagesFree += "\n";
  }
}

if (Object.keys(noDriverCarsByCompany).length > 0) {
  combinedMessagesNoDriver = "Текущие автомобили без водителя:\n\n";
  for (const [company, cars] of Object.entries(noDriverCarsByCompany)) {
    combinedMessagesNoDriver += `<b>${company}</b>: ${cars[0]}\n`;
    if (cars.length > 1) {
      combinedMessagesNoDriver += cars.slice(1).join("\n") + "\n";
    }
    combinedMessagesNoDriver += "\n";
  }
}

let finalMessage = (combinedMessagesFree + combinedMessagesNoDriver).trim();

if (!finalMessage) {
  finalMessage = "На данный момент нет свободных автомобилей и автомобилей без водителей";
}

return [{ json: { combinedMessages: finalMessage } }];

