// Функция для получения номера недели
function getWeek(date) {
    const current = new Date(date);
    const start = new Date(current.getFullYear(), 0, 1);
    const diff = (current - start + (start.getTimezoneOffset() - current.getTimezoneOffset()) * 60000) / 86400000;
    return Math.floor((diff + start.getDay() + 1) / 7);
}

// Функция для получения начала недели (понедельник)
function getStartOfWeek(date) {
    const current = new Date(date);
    const first = current.getDate() - current.getDay() + 1; // Понедельник
    return new Date(current.setDate(first));
}

// Функция для получения конца недели (воскресенье)
function getEndOfWeek(date) {
    const current = new Date(date);
    const last = current.getDate() - current.getDay() + 7; // Воскресенье
    return new Date(current.setDate(last));
}

// Функция для получения текущего месяца
function getCurrentMonth() {
    const today = new Date();
    return today.getMonth(); // возвращает номер месяца (0-11)
}

// Функция для фильтрации данных по текущему месяцу
function filterByCurrentMonth(data) {
    const currentMonth = getCurrentMonth();
    return data.filter(entry => {
        const entryMonth = new Date(entry.date).getMonth();
        return entryMonth === currentMonth;
    });
}

// Группируем данные по неделям, учитывая только текущий месяц
function groupByWeek(data) {
    const weeks = {};
    data.forEach(entry => {
        const weekNumber = getWeek(entry.date);
        if (!weeks[weekNumber]) {
            weeks[weekNumber] = [];
        }
        weeks[weekNumber].push(entry);
