// –ü–æ–ª—É—á–∞–µ–º –≤—Ö–æ–¥–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ –∏–∑ –ø—Ä–µ–¥—ã–¥—É—â–µ–≥–æ –∫—É–±–∏–∫–∞
const inputData = items.map(item => item.json);

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –¥–æ–±–∞–≤–ª–µ–Ω–∏—è 10 —á–∞—Å–æ–≤ –∫–æ –≤—Ä–µ–º–µ–Ω–∏
function addHoursToTime(timeStr, hours) {
    const dateObj = new Date(timeStr.replace(" ", "T")); // –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º —Å—Ç—Ä–æ–∫—É –≤—Ä–µ–º–µ–Ω–∏ –≤ –æ–±—ä–µ–∫—Ç Date
    dateObj.setHours(dateObj.getHours() + hours); // –î–æ–±–∞–≤–ª—è–µ–º 10 —á–∞—Å–æ–≤
    return dateObj.toISOString().replace("T", " ").split(".")[0]; // –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º –æ–±—Ä–∞—Ç–Ω–æ –≤ —Å—Ç—Ä–æ–∫—É –∏ —É–±–∏—Ä–∞–µ–º –º–∏–ª–ª–∏—Å–µ–∫—É–Ω–¥—ã
}

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–æ–¥—Å—á–µ—Ç–∞ –∑–∞—Ä–∞–±–æ—Ç–∫–∞ –∏ –∫–æ–ª–∏—á–µ—Å—Ç–≤–∞ —Ä–µ–π—Å–æ–≤ –ø–æ –¥–∞—Ç–∞–º
function calculateTotalsByDate(data) {
    const totalsByDate = {};

    data.forEach(entry => {
        entry.data.forEach(round => {
            const { driver_rate, driver_with_trailer, time_of_start } = round;

            // –î–æ–±–∞–≤–ª—è–µ–º 10 —á–∞—Å–æ–≤ –∫–æ –≤—Ä–µ–º–µ–Ω–∏
            const adjustedTime = addHoursToTime(time_of_start, 5);

            // –ò–∑–≤–ª–µ–∫–∞–µ–º —Ç–æ–ª—å–∫–æ –¥–∞—Ç—É –∏–∑ —Å–∫–æ—Ä—Ä–µ–∫—Ç–∏—Ä–æ–≤–∞–Ω–Ω–æ–≥–æ –≤—Ä–µ–º–µ–Ω–∏
            const date = adjustedTime.split(" ")[0];

            // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∏—Ä—É–µ–º –¥–∞–Ω–Ω—ã–µ –¥–ª—è –∫–∞–∂–¥–æ–π –Ω–æ–≤–æ–π –¥–∞—Ç—ã
            if (!totalsByDate[date]) {
                totalsByDate[date] = {
                    total_count: 0,  // –û–±—â–µ–µ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ä–µ–π—Å–æ–≤
                    total_earnings: 0  // –û–±—â–∏–π –∑–∞—Ä–∞–±–æ—Ç–æ–∫
                };
            }

            // –£–≤–µ–ª–∏—á–∏–≤–∞–µ–º –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ä–µ–π—Å–æ–≤
            totalsByDate[date].total_count++;

            // –£–≤–µ–ª–∏—á–∏–≤–∞–µ–º —Å—É–º–º—É –∑–∞—Ä–∞–±–æ—Ç–∫–∞
            totalsByDate[date].total_earnings += driver_rate;
        });
    });

    // –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º –æ–±—ä–µ–∫—Ç –≤ –º–∞—Å—Å–∏–≤ –¥–ª—è —É–¥–æ–±—Å—Ç–≤–∞ –æ–±—Ä–∞–±–æ—Ç–∫–∏
    return Object.keys(totalsByDate).map(date => ({
        date: date,
        total_count: totalsByDate[date].total_count,
        total_earnings: totalsByDate[date].total_earnings
    }));
}

// –û–±—Ä–∞–±–∞—Ç—ã–≤–∞–µ–º –¥–∞–Ω–Ω—ã–µ
const processedData = calculateTotalsByDate(inputData);

// –í–æ–∑–≤—Ä–∞—â–∞–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç –¥–ª—è —Å–ª–µ–¥—É—é—â–µ–≥–æ –∫—É–±–∏–∫–∞
return processedData.map(entry => ({ json: entry }));

// –ü–æ–ª—É—á–∞–µ–º –≤—Ö–æ–¥–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ –∏–∑ –ø—Ä–µ–¥—ã–¥—É—â–µ–≥–æ –∫—É–±–∏–∫–∞
const inputData = items.map(item => item.json);

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –æ–±—Ä–∞–±–æ—Ç–∫–∏ –¥–∞–Ω–Ω—ã—Ö
function calculateTotals(data) {
    return data.map(entry => {
        const { id, quarry_hours, price_for_driver, date, start_time } = entry;

        // –ï—Å–ª–∏ price_for_driver —Ä–∞–≤–µ–Ω 0 –∏–ª–∏ false, –∑–∞—Ä–∞–±–æ—Ç–æ–∫ —Ä–∞–≤–µ–Ω 0
        if (!price_for_driver) {
            // –î–æ–±–∞–≤–ª—è–µ–º 10 —á–∞—Å–æ–≤ –∫ –¥–∞—Ç–µ
            const adjustedDate = new Date(start_time);
            adjustedDate.setHours(adjustedDate.getHours() + 5); // –ü—Ä–∏–±–∞–≤–ª—è–µ–º 10 —á–∞—Å–æ–≤

            return {
                id: id,
                quarry_hours: quarry_hours,  // –ö–æ–ª–∏—á–µ—Å—Ç–≤–æ –∫–∞—Ä—å–µ—Ä–Ω—ã—Ö —á–∞—Å–æ–≤
                total: 0,  // –ó–∞—Ä–∞–±–æ—Ç–æ–∫ = 0
                date: adjustedDate.toISOString(),  // –î–ª—è –≤—ã–≤–æ–¥–∞ –∏—Å–ø–æ–ª—å–∑—É–µ–º —Å–∫–æ—Ä—Ä–µ–∫—Ç–∏—Ä–æ–≤–∞–Ω–Ω—É—é –¥–∞—Ç—É
                original_date: date  // –°–æ—Ö—Ä–∞–Ω—è–µ–º –æ—Ä–∏–≥–∏–Ω–∞–ª—å–Ω—É—é –¥–∞—Ç—É
            };
        }

        // –¶–µ–Ω–∞ –≤–æ–¥–∏—Ç–µ–ª—è —É–∂–µ –∏—Ç–æ–≥–æ–≤–∞—è, –ø—Ä–æ—Å—Ç–æ –ø–µ—Ä–µ–¥–∞–µ–º –µ—ë –≤ —Ä–µ–∑—É–ª—å—Ç–∞—Ç
        const total = parseFloat(price_for_driver);

        // –î–æ–±–∞–≤–ª—è–µ–º 10 —á–∞—Å–æ–≤ –∫ –¥–∞—Ç–µ
        const adjustedDate = new Date(start_time);
        adjustedDate.setHours(adjustedDate.getHours() + 5); // –ü—Ä–∏–±–∞–≤–ª—è–µ–º 10 —á–∞—Å–æ–≤

        return {
            id: id,
            quarry_hours: quarry_hours,  // –ö–æ–ª–∏—á–µ—Å—Ç–≤–æ –∫–∞—Ä—å–µ—Ä–Ω—ã—Ö —á–∞—Å–æ–≤
            total: total,  // –û–±—â–∏–π –∑–∞—Ä–∞–±–æ—Ç–æ–∫ = price_for_driver
            date: adjustedDate.toISOString(),  // –î–ª—è –≤—ã–≤–æ–¥–∞ –∏—Å–ø–æ–ª—å–∑—É–µ–º —Å–∫–æ—Ä—Ä–µ–∫—Ç–∏—Ä–æ–≤–∞–Ω–Ω—É—é –¥–∞—Ç—É
            original_date: date  // –°–æ—Ö—Ä–∞–Ω—è–µ–º –æ—Ä–∏–≥–∏–Ω–∞–ª—å–Ω—É—é –¥–∞—Ç—É
        };
    });
}

// –û–±—Ä–∞–±–∞—Ç—ã–≤–∞–µ–º –≤—Ö–æ–¥–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ
const processedData = calculateTotals(inputData);

// –í–æ–∑–≤—Ä–∞—â–∞–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç –≤ —Ñ–æ—Ä–º–∞—Ç–µ JSON –¥–ª—è —Å–ª–µ–¥—É—é—â–µ–≥–æ –∫—É–±–∏–∫–∞
return processedData.map(entry => ({ json: entry }));

// –ü–æ–ª—É—á–∞–µ–º –≤—Ö–æ–¥–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ —á–µ—Ä–µ–∑ items
const inputData = items.map(item => item.json);

// –ó–∞–¥–∞–µ–º —Ç–µ–∫—É—â—É—é –¥–∞—Ç—É –¥–ª—è —Ç–µ—Å—Ç–∏—Ä–æ–≤–∞–Ω–∏—è
const today = new Date(); //'2024-11-02T00:00:00'
today.setMinutes(today.getMinutes() + today.getTimezoneOffset() + (5 * 60));

// –û–ø—Ä–µ–¥–µ–ª—è–µ–º –Ω–∞—á–∞–ª–æ —Ç–µ–∫—É—â–µ–π –Ω–µ–¥–µ–ª–∏ (–ø–æ–Ω–µ–¥–µ–ª—å–Ω–∏–∫ —Ç–µ–∫—É—â–µ–π –Ω–µ–¥–µ–ª–∏)
const startOfWeek = new Date(today);
startOfWeek.setDate(today.getDate() - today.getDay() + (today.getDay() === 0 ? -6 : 1)); // –£—á–∏—Ç—ã–≤–∞–µ–º –≤–æ—Å–∫—Ä–µ—Å–µ–Ω—å–µ –∫–∞–∫ –ø–æ—Å–ª–µ–¥–Ω–∏–π –¥–µ–Ω—å –Ω–µ–¥–µ–ª–∏

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –æ–±–Ω—É–ª–µ–Ω–∏—è –≤—Ä–µ–º–µ–Ω–∏ –≤ –¥–∞—Ç–µ
function resetTime(date) {
    const newDate = new Date(date);
    newDate.setHours(0, 0, 0, 0); // –£–±–∏—Ä–∞–µ–º –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ã –≤—Ä–µ–º–µ–Ω–∏
    return newDate;
}

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è —Ñ–∏–ª—å—Ç—Ä–∞—Ü–∏–∏ –¥–∞–Ω–Ω—ã—Ö –ø–æ –¥–Ω—è–º
function getDailyReport(date) {
    let totalCount = 0;      // –ö–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ä–µ–π—Å–æ–≤
    let totalQuarryHours = 0; // –ö–∞—Ä—å–µ—Ä–Ω—ã–µ —á–∞—Å—ã
    let totalEarnings = 0;    // –û–±—â–∏–π –∑–∞—Ä–∞–±–æ—Ç–æ–∫ –∑–∞ –¥–µ–Ω—å

    const resetDate = resetTime(date);

    inputData.forEach(entry => {
        const entryDate = resetTime(new Date(entry.date));

        // –°—Ä–∞–≤–Ω–∏–≤–∞–µ–º –¥–∞—Ç—ã –±–µ–∑ —É—á–µ—Ç–∞ –≤—Ä–µ–º–µ–Ω–∏
        if (entryDate.toISOString().split('T')[0] === resetDate.toISOString().split('T')[0]) {
            if (entry.total_count !== undefined && entry.total_earnings !== undefined) {
                totalCount += entry.total_count;  // –°—É–º–º–∏—Ä—É–µ–º –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ä–µ–π—Å–æ–≤
                totalEarnings += entry.total_earnings; // –°—É–º–º–∏—Ä—É–µ–º –∑–∞—Ä–∞–±–æ—Ç–æ–∫
            } else if (entry.quarry_hours !== undefined && entry.total !== undefined) {
                totalQuarryHours += entry.quarry_hours; // –°—É–º–º–∏—Ä—É–µ–º –∫–∞—Ä—å–µ—Ä–Ω—ã–µ —á–∞—Å—ã
                totalEarnings += entry.total; // –°—É–º–º–∏—Ä—É–µ–º –∑–∞—Ä–∞–±–æ—Ç–æ–∫ –∑–∞ —Ä–∞–±–æ—Ç—ã –≤ –∫–∞—Ä—å–µ—Ä–µ
            }
        }
    });

    return {
        count: totalCount,
        quarry_hours: totalQuarryHours,
        total: totalEarnings
    };
}

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –≥–µ–Ω–µ—Ä–∞—Ü–∏–∏ –æ—Ç—á–µ—Ç–∞ –ø–æ –¥–Ω—è–º –Ω–µ–¥–µ–ª–∏
function getCurrentWeekReport() {
    let textMessage = 'üìä <b>–û—Ç—á–µ—Ç –∑–∞ —Ç–µ–∫—É—â—É—é –Ω–µ–¥–µ–ª—é</b>\n\n';

    let currentDate = new Date(today);
    while (currentDate >= startOfWeek) {
        const dayReport = getDailyReport(currentDate);
        const dayOfWeek = currentDate.toLocaleDateString('ru-RU', { weekday: 'long' });
        const dateStr = currentDate.toLocaleDateString('ru-RU', { day: '2-digit', month: '2-digit' });

        textMessage += `üìÖ <b>${dayOfWeek} (${dateStr}):</b>\n`;
        if (dayReport.count === 0 && dayReport.quarry_hours === 0) {
            textMessage += '–ù–µ –±—ã–ª–æ –ø–µ—Ä–µ–≤–æ–∑–æ–∫\n\n';
        } else {
            textMessage += `üöö –ö–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ä–µ–π—Å–æ–≤: ${dayReport.count}\n`;
            textMessage += `üïë –ö–∞—Ä—å–µ—Ä–Ω—ã–µ —á–∞—Å—ã: ${dayReport.quarry_hours} —á\n`;
            textMessage += `üíµ –ó–∞—Ä–∞–±–æ—Ç–æ–∫: ${dayReport.total} —Ä—É–±.\n\n`;
        }

        // –ü–µ—Ä–µ—Ö–æ–¥–∏–º –∫ –ø—Ä–µ–¥—ã–¥—É—â–µ–º—É –¥–Ω—é
        currentDate.setDate(currentDate.getDate() - 1);
    }

    return textMessage;
}

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–æ–ª—É—á–µ–Ω–∏—è –æ—Ç—á–µ—Ç–∞ –∑–∞ –º–µ—Å—è—Ü
function getMonthlyReport(startDate, endDate) {
    let totalCount = 0;
    let totalQuarryHours = 0;
    let totalEarnings = 0;

    const start = resetTime(startDate);
    const end = resetTime(endDate);

    inputData.forEach(entry => {
        const entryDate = resetTime(new Date(entry.date));

        if (entryDate >= start && entryDate <= end) {
            if (entry.total_count !== undefined && entry.total_earnings !== undefined) {
                totalCount += entry.total_count;
                totalEarnings += entry.total_earnings;
            } else if (entry.quarry_hours !== undefined && entry.total !== undefined) {
                totalQuarryHours += entry.quarry_hours;
                totalEarnings += entry.total;
            }
        }
    });

    return {
        count: totalCount,
        quarry_hours: totalQuarryHours,
        total: totalEarnings
    };
}

// –û–ø—Ä–µ–¥–µ–ª—è–µ–º –Ω–∞—á–∞–ª–æ –∏ –∫–æ–Ω–µ—Ü —Ç–µ–∫—É—â–µ–≥–æ –º–µ—Å—è—Ü–∞
const startOfMonth = new Date(today.getFullYear(), today.getMonth(), 1);
const endOfMonth = new Date(today.getFullYear(), today.getMonth() + 1, 0); // –ü–æ—Å–ª–µ–¥–Ω–∏–π –¥–µ–Ω—å –º–µ—Å—è—Ü–∞

// –ü–æ–ª—É—á–∞–µ–º –æ—Ç—á–µ—Ç –∑–∞ —Ç–µ–∫—É—â–∏–π –º–µ—Å—è—Ü
const currentMonthReport = getMonthlyReport(startOfMonth, today);

// –ü—Ä–æ–≤–µ—Ä–∫–∞ –Ω–∞ –≤—ã–≤–æ–¥ –æ—Ç—á–µ—Ç–∞ –∑–∞ –ø—Ä–æ—à–ª—ã–π –º–µ—Å—è—Ü
let previousMonthText = '';
const firstOfMonth = new Date(today.getFullYear(), today.getMonth(), 1);
const isInFirstTenDays = today >= firstOfMonth && today < new Date(today.getFullYear(), today.getMonth(), 11, 5, 0, 0);

// –ï—Å–ª–∏ –Ω—É–∂–Ω–æ –≤—ã–≤–µ—Å—Ç–∏ –æ—Ç—á–µ—Ç –∑–∞ –ø—Ä–æ—à–ª—ã–π –º–µ—Å—è—Ü
if (isInFirstTenDays) {
    const startOfPreviousMonth = new Date(today.getFullYear(), today.getMonth() - 1, 1);
    const endOfPreviousMonth = new Date(today.getFullYear(), today.getMonth(), 0); // –ü–æ—Å–ª–µ–¥–Ω–∏–π –¥–µ–Ω—å –ø—Ä–æ—à–ª–æ–≥–æ –º–µ—Å—è—Ü–∞

    const previousMonthReport = getMonthlyReport(startOfPreviousMonth, endOfPreviousMonth);

    previousMonthText = `
üìä <b>–û—Ç—á–µ—Ç –∑–∞ –ø—Ä–æ—à–ª—ã–π –º–µ—Å—è—Ü</b>

üöö –ö–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ä–µ–π—Å–æ–≤: ${previousMonthReport.count}
üïë –ö–∞—Ä—å–µ—Ä–Ω—ã–µ —á–∞—Å—ã: ${previousMonthReport.quarry_hours} —á
üíµ –ó–∞—Ä–∞–±–æ—Ç–æ–∫: ${previousMonthReport.total} —Ä—É–±.\n`;
}

// –§–æ—Ä–º–∏—Ä—É–µ–º –æ—Ç—á–µ—Ç –∑–∞ —Ç–µ–∫—É—â—É—é –Ω–µ–¥–µ–ª—é
let textMessage = getCurrentWeekReport();

// –§–æ—Ä–º–∏—Ä—É–µ–º –æ—Ç—á–µ—Ç –∑–∞ —Ç–µ–∫—É—â–∏–π –º–µ—Å—è—Ü
let currentMonthText = `üìä <b>–û—Ç—á–µ—Ç –∑–∞ —Ç–µ–∫—É—â–∏–π –º–µ—Å—è—Ü</b>

üöö –ö–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ä–µ–π—Å–æ–≤: ${currentMonthReport.count}
üïë –ö–∞—Ä—å–µ—Ä–Ω—ã–µ —á–∞—Å—ã: ${currentMonthReport.quarry_hours} —á
üíµ –ó–∞—Ä–∞–±–æ—Ç–æ–∫: ${currentMonthReport.total} —Ä—É–±.\n`;

// –î–æ–±–∞–≤–ª—è–µ–º –º–µ—Å—è—á–Ω—ã–µ –æ—Ç—á–µ—Ç—ã
textMessage += currentMonthText;

// –î–æ–±–∞–≤–ª—è–µ–º –æ—Ç—á–µ—Ç –∑–∞ –ø—Ä–æ—à–ª—ã–π –º–µ—Å—è—Ü –≤ —Å–∞–º–æ–º –∫–æ–Ω—Ü–µ, –µ—Å–ª–∏ —ç—Ç–æ –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ
if (previousMonthText) {
    textMessage += previousMonthText;
}

// –í–æ–∑–≤—Ä–∞—â–∞–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç –≤ —Ñ–æ—Ä–º–∞—Ç–µ JSON
return [
    {
        json: {
            message: textMessage
        }
    }
];

// –ü–æ–ª—É—á–∞–µ–º –¥–∞–Ω–Ω—ã–µ –∏–∑ –ø—Ä–µ–¥—ã–¥—É—â–µ–π –Ω–æ–¥—ã
const items = $input.all();

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–æ–ª—É—á–µ–Ω–∏—è –º–µ—Å—è—Ü–∞ –∏–∑ –¥–∞—Ç—ã –≤ —Ñ–æ—Ä–º–∞—Ç–µ "YYYY-MM"
const getMonth = (date) => {
  const d = new Date(date);
  const year = d.getFullYear();
  const month = ('0' + (d.getMonth() + 1)).slice(-2); // –î–æ–±–∞–≤–ª—è–µ–º –≤–µ–¥—É—â–∏–π –Ω–æ–ª—å –∫ –º–µ—Å—è—Ü—É, –µ—Å–ª–∏ –Ω—É–∂–Ω–æ
  return `${year}-${month}`;
};

// –û–±—Ä–∞–±–∞—Ç—ã–≤–∞–µ–º –∫–∞–∂–¥—É—é –∑–∞–ø–∏—Å—å
const result = items.map(item => {
  const data = item.json;

  // –ü–æ–ª—É—á–∞–µ–º –º–µ—Å—è—Ü –∏–∑ date_start (–¥–∞—Ç–∞ —Å—Ç–∞—Ä—Ç–∞)
  const month = getMonth(data.date_start);

  // –í–æ–∑–≤—Ä–∞—â–∞–µ–º –ø—Ä–µ–æ–±—Ä–∞–∑–æ–≤–∞–Ω–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ
  return {
    id: data.id,
    date: month, // –ú–µ—Å—è—Ü, —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤—É—é—â–∏–π –¥–∞—Ç–µ —Å—Ç–∞—Ä—Ç–∞
    required_bonus_amount: data.required_bonus_amount,
    required_fine_amount: data.required_fine_amount,
    required_comment: data.required_comment
  };
});

return result;

// –ü–æ–ª—É—á–∞–µ–º –≤—Ö–æ–¥–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ –∏–∑ –ø—Ä–µ–¥—ã–¥—É—â–µ–≥–æ –∫—É–±–∏–∫–∞
const inputData = items.map(item => item.json);

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –¥–æ–±–∞–≤–ª–µ–Ω–∏—è 10 —á–∞—Å–æ–≤ –∫–æ –≤—Ä–µ–º–µ–Ω–∏
function addHoursToTime(timeStr, hours) {
    const dateObj = new Date(timeStr.replace(" ", "T")); // –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º —Å—Ç—Ä–æ–∫—É –≤—Ä–µ–º–µ–Ω–∏ –≤ –æ–±—ä–µ–∫—Ç Date
    dateObj.setHours(dateObj.getHours() + hours); // –î–æ–±–∞–≤–ª—è–µ–º 10 —á–∞—Å–æ–≤
    return dateObj.toISOString().replace("T", " ").split(".")[0]; // –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º –æ–±—Ä–∞—Ç–Ω–æ –≤ —Å—Ç—Ä–æ–∫—É –∏ —É–±–∏—Ä–∞–µ–º –º–∏–ª–ª–∏—Å–µ–∫—É–Ω–¥—ã
}

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–æ–¥—Å—á–µ—Ç–∞ –∑–∞—Ä–∞–±–æ—Ç–∫–∞ –∏ –∫–æ–ª–∏—á–µ—Å—Ç–≤–∞ —Ä–µ–π—Å–æ–≤ –ø–æ –¥–∞—Ç–∞–º
function calculateTotalsByDate(data) {
    const totalsByDate = {};

    data.forEach(entry => {
        entry.data.forEach(round => {
            const { driver_rate, driver_with_trailer, time_of_start } = round;

            // –î–æ–±–∞–≤–ª—è–µ–º 10 —á–∞—Å–æ–≤ –∫–æ –≤—Ä–µ–º–µ–Ω–∏
            const adjustedTime = addHoursToTime(time_of_start, 5);

            // –ò–∑–≤–ª–µ–∫–∞–µ–º —Ç–æ–ª—å–∫–æ –¥–∞—Ç—É –∏–∑ —Å–∫–æ—Ä—Ä–µ–∫—Ç–∏—Ä–æ–≤–∞–Ω–Ω–æ–≥–æ –≤—Ä–µ–º–µ–Ω–∏
            const date = adjustedTime.split(" ")[0];

            // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∏—Ä—É–µ–º –¥–∞–Ω–Ω—ã–µ –¥–ª—è –∫–∞–∂–¥–æ–π –Ω–æ–≤–æ–π –¥–∞—Ç—ã
            if (!totalsByDate[date]) {
                totalsByDate[date] = {
                    total_count: 0,  // –û–±—â–µ–µ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ä–µ–π—Å–æ–≤
                    total_earnings: 0  // –û–±—â–∏–π –∑–∞—Ä–∞–±–æ—Ç–æ–∫
                };
            }

            // –£–≤–µ–ª–∏—á–∏–≤–∞–µ–º –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ä–µ–π—Å–æ–≤
            totalsByDate[date].total_count++;

            // –£–≤–µ–ª–∏—á–∏–≤–∞–µ–º —Å—É–º–º—É –∑–∞—Ä–∞–±–æ—Ç–∫–∞
            totalsByDate[date].total_earnings += driver_rate;
        });
    });

    // –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º –æ–±—ä–µ–∫—Ç –≤ –º–∞—Å—Å–∏–≤ –¥–ª—è —É–¥–æ–±—Å—Ç–≤–∞ –æ–±—Ä–∞–±–æ—Ç–∫–∏
    return Object.keys(totalsByDate).map(date => ({
        date: date,
        total_count: totalsByDate[date].total_count,
        total_earnings: totalsByDate[date].total_earnings
    }));
}

