                total: 0,  // –ó–∞—Ä–∞–±–æ—Ç–æ–∫ = 0
                date: adjustedDate.toISOString(),  // –î–ª—è –≤—ã–≤–æ–¥–∞ –∏—Å–ø–æ–ª—å–∑—É–µ–º —Å–∫–æ—Ä—Ä–µ–∫—Ç–∏—Ä–æ–≤–∞–Ω–Ω—É—é –¥–∞—Ç—É
                original_date: date  // –°–æ—Ö—Ä–∞–Ω—è–µ–º –æ—Ä–∏–≥–∏–Ω–∞–ª—å–Ω—É—é –¥–∞—Ç—É
            };
        }

        // –¶–µ–Ω–∞ –≤–æ–¥–∏—Ç–µ–ª—è —É–∂–µ –∏—Ç–æ–≥–æ–≤–∞—è, –ø—Ä–æ—Å—Ç–æ –ø–µ—Ä–µ–¥–∞–µ–º –µ—ë –≤ —Ä–µ–∑—É–ª—å—Ç–∞—Ç
        const total = parseFloat(price_for_driver);

        // –î–æ–±–∞–≤–ª—è–µ–º 10 —á–∞—Å–æ–≤ –∫ –¥–∞—Ç–µ
        const adjustedDate = new Date(start_time);
        adjustedDate.setHours(adjustedDate.getHours() + 5); // –ü—Ä–∏–±–∞–≤–ª—è–µ–º 10 —á–∞—Å–æ–≤

        return {
            id: id,
            quarry_hours: quarry_hours,  // –ö–æ–ª–∏—á–µ—Å—Ç–≤–æ –∫–∞—Ä—å–µ—Ä–Ω—ã—Ö —á–∞—Å–æ–≤
            total: total,  // –û–±—â–∏–π –∑–∞—Ä–∞–±–æ—Ç–æ–∫ = price_for_driver
            date: adjustedDate.toISOString(),  // –î–ª—è –≤—ã–≤–æ–¥–∞ –∏—Å–ø–æ–ª—å–∑—É–µ–º —Å–∫–æ—Ä—Ä–µ–∫—Ç–∏—Ä–æ–≤–∞–Ω–Ω—É—é –¥–∞—Ç—É
            original_date: date  // –°–æ—Ö—Ä–∞–Ω—è–µ–º –æ—Ä–∏–≥–∏–Ω–∞–ª—å–Ω—É—é –¥–∞—Ç—É
        };
    });
}

// –û–±—Ä–∞–±–∞—Ç—ã–≤–∞–µ–º –≤—Ö–æ–¥–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ
const processedData = calculateTotals(inputData);

// –í–æ–∑–≤—Ä–∞—â–∞–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç –≤ —Ñ–æ—Ä–º–∞—Ç–µ JSON –¥–ª—è —Å–ª–µ–¥—É—é—â–µ–≥–æ –∫—É–±–∏–∫–∞
return processedData.map(entry => ({ json: entry }));

// –ü–æ–ª—É—á–∞–µ–º –≤—Ö–æ–¥–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ —á–µ—Ä–µ–∑ items
const inputData = items.map(item => item.json);

// –ó–∞–¥–∞–µ–º —Ç–µ–∫—É—â—É—é –¥–∞—Ç—É —Å —É—á–µ—Ç–æ–º —á–∞—Å–æ–≤–æ–≥–æ –ø–æ—è—Å–∞ –í–ª–∞–¥–∏–≤–æ—Å—Ç–æ–∫–∞
const today = new Date('2024-10-07T00:00:00');//'2024-10-07T00:00:00'
today.setMinutes(today.getMinutes() + today.getTimezoneOffset() + (5 * 60)); // UTC +10 –í–ª–∞–¥–∏–≤–æ—Å—Ç–æ–∫

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –æ–±–Ω—É–ª–µ–Ω–∏—è –≤—Ä–µ–º–µ–Ω–∏ –≤ –¥–∞—Ç–µ
function resetTime(date) {
    const newDate = new Date(date);
    newDate.setHours(0, 0, 0, 0); // –£–±–∏—Ä–∞–µ–º –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ã –≤—Ä–µ–º–µ–Ω–∏
    return newDate;
}

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è —Ñ–∏–ª—å—Ç—Ä–∞—Ü–∏–∏ –¥–∞–Ω–Ω—ã—Ö –ø–æ –¥–Ω—è–º
function getDailyReport(date) {
    let totalCount = 0;      // –ö–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ä–µ–π—Å–æ–≤
    let totalQuarryHours = 0; // –ö–∞—Ä—å–µ—Ä–Ω—ã–µ —á–∞—Å—ã
    let totalEarnings = 0;    // –û–±—â–∏–π –∑–∞—Ä–∞–±–æ—Ç–æ–∫ –∑–∞ –¥–µ–Ω—å

    const resetDate = resetTime(date);

    inputData.forEach(entry => {
        const entryDate = resetTime(new Date(entry.date));

        // –°—Ä–∞–≤–Ω–∏–≤–∞–µ–º –¥–∞—Ç—ã –±–µ–∑ —É—á–µ—Ç–∞ –≤—Ä–µ–º–µ–Ω–∏
        if (entryDate.toISOString().split('T')[0] === resetDate.toISOString().split('T')[0]) {
            if (entry.total_count !== undefined && entry.total_earnings !== undefined) {
                totalCount += entry.total_count;  // –°—É–º–º–∏—Ä—É–µ–º –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ä–µ–π—Å–æ–≤
                totalEarnings += entry.total_earnings; // –°—É–º–º–∏—Ä—É–µ–º –∑–∞—Ä–∞–±–æ—Ç–æ–∫
            } else if (entry.quarry_hours !== undefined && entry.total !== undefined) {
                totalQuarryHours += entry.quarry_hours; // –°—É–º–º–∏—Ä—É–µ–º –∫–∞—Ä—å–µ—Ä–Ω—ã–µ —á–∞—Å—ã
                totalEarnings += entry.total; // –°—É–º–º–∏—Ä—É–µ–º –∑–∞—Ä–∞–±–æ—Ç–æ–∫ –∑–∞ —Ä–∞–±–æ—Ç—ã –≤ –∫–∞—Ä—å–µ—Ä–µ
            }
        }
    });
    return {
        count: totalCount,
        quarry_hours: totalQuarryHours,
        total: totalEarnings
    };
}

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–æ–ª—É—á–µ–Ω–∏—è –æ—Ç—á–µ—Ç–∞ –∑–∞ –Ω–µ–¥–µ–ª—é
function getWeeklyReport(startDate, endDate) {
    let totalCount = 0;
    let totalQuarryHours = 0;
    let totalEarnings = 0;

    const start = resetTime(startDate);
    const end = resetTime(endDate);

    inputData.forEach(entry => {
        const entryDate = resetTime(new Date(entry.date));

        if (entryDate >= start && entryDate <= end) {
            if (entry.total_count !== undefined && entry.total_earnings !== undefined) {
                totalCount += entry.total_count;
                totalEarnings += entry.total_earnings;
            } else if (entry.quarry_hours !== undefined && entry.total !== undefined) {
                totalQuarryHours += entry.quarry_hours;
                totalEarnings += entry.total;
            }
        }
    });
    return {
        count: totalCount,
        quarry_hours: totalQuarryHours,
        total: totalEarnings
    };
}

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –≥–µ–Ω–µ—Ä–∞—Ü–∏–∏ —Å—Ä–µ–∑–æ–≤ –Ω–µ–¥–µ–ª–∏, –≥–¥–µ —Å—Ä–µ–∑—ã –∑–∞–∫–∞–Ω—á–∏–≤–∞—é—Ç—Å—è –≤–æ—Å–∫—Ä–µ—Å–µ–Ω—å–µ–º
function getWeeklySlices(startDate, endDate) {
    let currentWeekStart = new Date(startDate);
    let weeklyReports = [];
    let totalWeeklyEarnings = 0;

    while (currentWeekStart <= endDate) {
        let currentWeekEnd = new Date(currentWeekStart);

        // –û–ø—Ä–µ–¥–µ–ª—è–µ–º –¥–µ–Ω—å –Ω–µ–¥–µ–ª–∏ (0 - –≤–æ—Å–∫—Ä–µ—Å–µ–Ω—å–µ, 6 - —Å—É–±–±–æ—Ç–∞)
        const dayOfWeek = currentWeekStart.getDay();

        // –ï—Å–ª–∏ —ç—Ç–æ –ø–µ—Ä–≤—ã–π –¥–µ–Ω—å, –∏ –æ–Ω –Ω–µ –ø–æ–Ω–µ–¥–µ–ª—å–Ω–∏–∫, –¥–µ–ª–∞–µ–º —Å—Ä–µ–∑ —Ç–æ–ª—å–∫–æ –∑–∞ –æ–¥–∏–Ω –¥–µ–Ω—å
        if (dayOfWeek === 0) {
            currentWeekEnd = new Date(currentWeekStart); // –í–æ—Å–∫—Ä–µ—Å–µ–Ω—å–µ, —Å—Ä–µ–∑ –∑–∞ 1 –¥–µ–Ω—å
        } else {
            currentWeekEnd.setDate(currentWeekStart.getDate() + (7 - dayOfWeek)); // –ó–∞–∫–∞–Ω—á–∏–≤–∞–µ–º —Å—Ä–µ–∑ –≤ –≤–æ—Å–∫—Ä–µ—Å–µ–Ω—å–µ
        }

        if (currentWeekEnd > endDate) {
            currentWeekEnd = new Date(endDate); // –û–≥—Ä–∞–Ω–∏—á–∏–≤–∞–µ–º –∫–æ–Ω–µ—Ü –Ω–µ–¥–µ–ª–∏ –ø–æ—Å–ª–µ–¥–Ω–∏–º –¥–Ω–µ–º
        }

        const report = getWeeklyReport(currentWeekStart, currentWeekEnd);
        totalWeeklyEarnings += report.total;

        // –§–æ—Ä–º–∏—Ä—É–µ–º —Ç–µ–∫—Å—Ç –Ω–µ–¥–µ–ª–∏ —Å –≤–µ–¥—É—â–∏–º–∏ –Ω—É–ª—è–º–∏ –¥–ª—è –¥–Ω—è –∏ –º–µ—Å—è—Ü–∞
        let weekText = `üìÖ <b>–ù–µ–¥–µ–ª—è —Å ${String(currentWeekStart.getDate()).padStart(2, '0')}.${String(currentWeekStart.getMonth() + 1).padStart(2, '0')} –ø–æ ${String(currentWeekEnd.getDate()).padStart(2, '0')}.${String(currentWeekEnd.getMonth() + 1).padStart(2, '0')}:</b>\n`;
        weekText += `üöö –ö–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ä–µ–π—Å–æ–≤: ${report.count}\n`;
        weekText += `üïë –ö–∞—Ä—å–µ—Ä–Ω—ã–µ —á–∞—Å—ã: ${report.quarry_hours} —á\n`;
        weekText += `üíµ –ó–∞—Ä–∞–±–æ—Ç–æ–∫: ${report.total} —Ä—É–±.\n\n`;

        weeklyReports.push(weekText);
        currentWeekStart.setDate(currentWeekEnd.getDate() + 1); // –ù–∞—á–∏–Ω–∞–µ–º —Å–ª–µ–¥—É—é—â—É—é –Ω–µ–¥–µ–ª—é
    }

    return {
        weeklyText: weeklyReports.reverse().join(''),
        totalWeeklyEarnings: totalWeeklyEarnings
    };
}

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–æ–ª—É—á–µ–Ω–∏—è –æ—Ç—á–µ—Ç–∞ –∑–∞ –ø—Ä–æ—à–ª—ã–π –º–µ—Å—è—Ü
function getPreviousMonthReport() {
    const previousMonth = new Date(today.getFullYear(), today.getMonth() - 1, 1);
    const endOfPreviousMonth = new Date(today.getFullYear(), today.getMonth(), 0); // –ü–æ—Å–ª–µ–¥–Ω–∏–π –¥–µ–Ω—å –ø—Ä–µ–¥—ã–¥—É—â–µ–≥–æ –º–µ—Å—è—Ü–∞
    return getWeeklySlices(previousMonth, endOfPreviousMonth);
}

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–æ–ª—É—á–µ–Ω–∏—è –æ—Ç—á–µ—Ç–∞ –∑–∞ —Ç–µ–∫—É—â–∏–π –º–µ—Å—è—Ü –¥–æ —Ç–µ–∫—É—â–µ–≥–æ –¥–Ω—è
function getCurrentMonthReport() {
    const startOfMonth = new Date(today.getFullYear(), today.getMonth(), 1);
    return getWeeklySlices(startOfMonth, today);
}

// –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–æ–¥—Å—á–µ—Ç–∞ –±–æ–Ω—É—Å–æ–≤, —à—Ç—Ä–∞—Ñ–æ–≤ –∏ –∫–æ–º–º–µ–Ω—Ç–∞—Ä–∏–µ–≤
function getMonthlyBonusesAndFines(month) {
    let bonusAmount = 0;
    let fineAmount = 0;
    let comments = [];

    inputData.forEach(entry => {
        const entryMonth = entry.date;

        if (entryMonth === month) {
            if (entry.required_bonus_amount && entry.required_bonus_amount !== 0) {
                bonusAmount += entry.required_bonus_amount;
            }

            if (entry.required_fine_amount && entry.required_fine_amount !== 0) {
                fineAmount += entry.required_fine_amount;
            }

            if (entry.required_comment && entry.required_comment !== false) {
                comments.push(entry.required_comment);
            }
        }
    });

    return {
        bonus: bonusAmount,
        fine: fineAmount,
        comments: comments
    };
}

