// Функция для обработки данных
function calculateTotals(data) {
    return data.map(entry => {
        const { id, quarry_hours, price_for_driver, date, start_time } = entry;

        // Если price_for_driver равен 0 или false, заработок равен 0
        if (!price_for_driver) {
            // Добавляем 10 часов к дате
            const adjustedDate = new Date(start_time);
            adjustedDate.setHours(adjustedDate.getHours() + 5); // Прибавляем 10 часов

            return {
                id: id,
                quarry_hours: quarry_hours,  // Количество карьерных часов
                total: 0,  // Заработок = 0
                date: adjustedDate.toISOString(),  // Для вывода используем скорректированную дату
                original_date: date  // Сохраняем оригинальную дату
            };
        }

        // Цена водителя уже итоговая, просто передаем её в результат
        const total = parseFloat(price_for_driver);

        // Добавляем 10 часов к дате
        const adjustedDate = new Date(start_time);
        adjustedDate.setHours(adjustedDate.getHours() + 5); // Прибавляем 10 часов

        return {
            id: id,
            quarry_hours: quarry_hours,  // Количество карьерных часов
            total: total,  // Общий заработок = price_for_driver
            date: adjustedDate.toISOString(),  // Для вывода используем скорректированную дату
            original_date: date  // Сохраняем оригинальную дату
        };
    });
}

