function resetTime(date) {
    const newDate = new Date(date);
    newDate.setHours(0, 0, 0, 0); // Убираем компоненты времени
    return newDate;
}

// Функция для фильтрации данных по дням
function getDailyReport(date) {
    let totalCount = 0;      // Количество рейсов
    let totalQuarryHours = 0; // Карьерные часы
    let totalEarnings = 0;    // Общий заработок за день

    const resetDate = resetTime(date);

    inputData.forEach(entry => {
        const entryDate = resetTime(new Date(entry.date));

        // Сравниваем даты без учета времени
        if (entryDate.toISOString().split('T')[0] === resetDate.toISOString().split('T')[0]) {
            if (entry.total_count !== undefined && entry.total_earnings !== undefined) {
                totalCount += entry.total_count;  // Суммируем количество рейсов
                totalEarnings += entry.total_earnings; // Суммируем заработок
            } else if (entry.quarry_hours !== undefined && entry.total !== undefined) {
                totalQuarryHours += entry.quarry_hours; // Суммируем карьерные часы
                totalEarnings += entry.total; // Суммируем заработок за работы в карьере
            }
        }
    });

    return {
        count: totalCount,
        quarry_hours: totalQuarryHours,
        total: totalEarnings
    };
}

// Функция для генерации отчета по дням недели
function getCurrentWeekReport() {
    let textMessage = '📊 <b>Отчет за текущую неделю</b>\n\n';

    let currentDate = new Date(today);
    while (currentDate >= startOfWeek) {
        const dayReport = getDailyReport(currentDate);
        const dayOfWeek = currentDate.toLocaleDateString('ru-RU', { weekday: 'long' });
        const dateStr = currentDate.toLocaleDateString('ru-RU', { day: '2-digit', month: '2-digit' });

        textMessage += `📅 <b>${dayOfWeek} (${dateStr}):</b>\n`;
        if (dayReport.count === 0 && dayReport.quarry_hours === 0) {
            textMessage += 'Не было перевозок\n\n';
        } else {
            textMessage += `🚚 Количество рейсов: ${dayReport.count}\n`;
            textMessage += `🕑 Карьерные часы: ${dayReport.quarry_hours} ч\n`;
            textMessage += `💵 Заработок: ${dayReport.total} руб.\n\n`;
        }

        // Переходим к предыдущему дню
        currentDate.setDate(currentDate.getDate() - 1);
    }

    return textMessage;
}

