// Функция для генерации срезов недели, где срезы заканчиваются воскресеньем
function getWeeklySlices(startDate, endDate) {
    let currentWeekStart = new Date(startDate);
    let weeklyReports = [];
    let totalWeeklyEarnings = 0;

    while (currentWeekStart <= endDate) {
        let currentWeekEnd = new Date(currentWeekStart);

        // Определяем день недели (0 - воскресенье, 6 - суббота)
        const dayOfWeek = currentWeekStart.getDay();

        // Если это первый день, и он не понедельник, делаем срез только за один день
        if (dayOfWeek === 0) {
            currentWeekEnd = new Date(currentWeekStart); // Воскресенье, срез за 1 день
        } else {
            currentWeekEnd.setDate(currentWeekStart.getDate() + (7 - dayOfWeek)); // Заканчиваем срез в воскресенье
        }

        if (currentWeekEnd > endDate) {
            currentWeekEnd = new Date(endDate); // Ограничиваем конец недели последним днем
        }

        const report = getWeeklyReport(currentWeekStart, currentWeekEnd);
        totalWeeklyEarnings += report.total;

        // Формируем текст недели с ведущими нулями для дня и месяца
        let weekText = `📅 <b>Неделя с ${String(currentWeekStart.getDate()).padStart(2, '0')}.${String(currentWeekStart.getMonth() + 1).padStart(2, '0')} по ${String(currentWeekEnd.getDate()).padStart(2, '0')}.${String(currentWeekEnd.getMonth() + 1).padStart(2, '0')}:</b>\n`;
        weekText += `🚚 Количество рейсов: ${report.count}\n`;
        weekText += `🕑 Карьерные часы: ${report.quarry_hours} ч\n`;
        weekText += `💵 Заработок: ${report.total} руб.\n\n`;

        weeklyReports.push(weekText);
        currentWeekStart.setDate(currentWeekEnd.getDate() + 1); // Начинаем следующую неделю
    }

    return {
        weeklyText: weeklyReports.reverse().join(''),
        totalWeeklyEarnings: totalWeeklyEarnings
    };
}

